import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AIPage.css'; // Make sure the CSS file is in the same directory

const AIPage = () => {
  const navigate = useNavigate();

  // States for form fields
  const [destination, setDestination] = useState('');
  const [gender, setGender] = useState('');
  const [fromDate, setFromDate] = useState('');
  const [toDate, setToDate] = useState('');
  const [season, setSeason] = useState('');
  const [budget, setBudget] = useState('');
  const [withPerson, setWithPerson] = useState('Family'); // Default to 'Family'
  const [children, setChildren] = useState('No'); // Default to 'No'

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission, e.g., make an API call
    console.log({ destination, gender, fromDate, toDate, season, budget, withPerson, children });

    // Example: navigate to another page after handling the form
    // navigate("/some-other-page");
  };

  return (
    <div className="ai-page-container">
      <h2 className="ai-title">AI Trip Advisor</h2>
      <form className="ai-form" onSubmit={handleSubmit}>
        <input type="text" placeholder="Destination" value={destination} onChange={e => setDestination(e.target.value)} />
        <input type="text" placeholder="Gender" value={gender} onChange={e => setGender(e.target.value)} />
        <input type="date" placeholder="From Date" value={fromDate} onChange={e => setFromDate(e.target.value)} />
        <input type="date" placeholder="To Date" value={toDate} onChange={e => setToDate(e.target.value)} />
        <input type="text" placeholder="Season" value={season} onChange={e => setSeason(e.target.value)} />
        <input type="number" placeholder="Budget" value={budget} onChange={e => setBudget(e.target.value)} />
        
        <select value={withPerson} onChange={e => setWithPerson(e.target.value)}>
          <option value="Family">Family</option>
          <option value="Solo">Solo</option>
          <option value="Friends">Friends</option>
          <option value="Couple">Couple</option>
        </select>

        <select value={children} onChange={e => setChildren(e.target.value)}>
          <option value="Yes">Yes</option>
          <option value="No">No</option>
        </select>
        
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AIPage;
