import React, { useState, useRef, useEffect } from 'react';
// Assuming loadGoogleMapsScript is a custom function you've defined to load the Google Maps script
import loadGoogleMapsScript from '../pages/googleMaps';
import './Profile.css'; // Ensure your CSS file is named correctly and placed in the right directory

const ProfileUpdatePage = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [profilePicture, setProfilePicture] = useState(null);

  const autocompleteInputFromRef = useRef(null);

  useEffect(() => {
    // Assuming you're using Google Places API to autocomplete an address field
    loadGoogleMapsScript(() => {
      if (window.google) {
        // Set up your autocomplete here
      }
    });
  }, []);

  const handleProfilePictureChange = (event) => {
    const file = event.target.files[0];
    file && setProfilePicture(URL.createObjectURL(file));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (password !== confirmPassword) {
      alert("Passwords don't match.");
      return;
    }
    // Submit these values to your backend here
    const formData = new FormData();
    formData.append('phoneNumber', phoneNumber);
    formData.append('email', email);
    formData.append('password', password); // Make sure to securely handle the password update
    // Include logic for profile picture update if necessary
    console.log('Profile would be updated here.');
  };

  return (
    <div className="profile-update-page">
      <h1>Update Profile</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="phone-number">Phone Number:</label>
          <input
            type="tel"
            id="phone-number"
            value={phoneNumber}
            onChange={e => setPhoneNumber(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="confirm-password">Confirm Password:</label>
          <input
            type="password"
            id="confirm-password"
            value={confirmPassword}
            onChange={e => setConfirmPassword(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="profile-picture">Profile Picture:</label>
          <input
            type="file"
            id="profile-picture"
            onChange={handleProfilePictureChange}
          />
          {profilePicture && (
            <img src={profilePicture} alt="Profile" className="profile-picture-preview" />
          )}
        </div>
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default ProfileUpdatePage;
