import React, { useState,useEffect } from 'react';
import List from './components/List'; 
import axios from 'axios';
import { baseURL } from './utils/constants';

const App = () => {
  const [input,setInput] = useState("");
  const [tasks,setTask] = useState([]);
  const [updateUI,setUpdateUI] = useState(false);
  const [updateId,setUpdateId] = useState(null);

  useEffect(() => {
    axios.get(`${baseURL}/get`)   ///receiving data
      .then((res)=>{
        console.log(res.data);
        setTask(res.data);         ///saving data
      })
  } ,[updateUI]);                ///updating UI

  const addTask = () => {
    axios.post(`${baseURL}/save`,{task: input})
    .then((res)=>{
      console.log(res.data);
      setInput("");
      setUpdateUI((prevState) => !prevState);
    });
  };

  const updateMode = (id,text) => {
    console.log(text);
    setInput(text);
    setUpdateId(id);
  };

  const updateTask= () => {
    axios.put(`$`)
  }

  return (
    <main align="center">
        <h1 className='title'>CRUD Operations </h1>
        <div className='inputholder'>
          <input type="text" value={input} onChange={(e) => setInput(e.target.value)} /> 
          <button type='submit' onClick={updateId? updateTask:addTask}>
              {
                updateId? "Update task":"Add task"
              }
          </button>
        </div> 
        <ul type='none'> 
          {
             tasks.map((task) => (
              <List key={task._id} id={task._id} task={task.task} setUpdateUI={setUpdateUI} updateMode={updateMode}/>
             ))
          }
        </ul>
    </main>
  )
}

export default App
